// Parse parametrÃ³w dla yearly API
const input = $input.first().json || {};
let urlParams = {};

if (input.query) urlParams = { ...urlParams, ...input.query };
if (input.year) urlParams = { ...urlParams, year: input.year };
if (input.body && typeof input.body === 'object') urlParams = { ...urlParams, ...input.body };

const now = new Date();
const currentYear = now.getFullYear();

// Walidacja roku
const inputYear = parseInt(urlParams.year);
const year = (inputYear >= 2020 && inputYear <= currentYear + 1) ? inputYear : currentYear;

console.log(`[YearlyAPI] Request for year ${year}`);

return [{
  json: {
    year: year,
    requestedAt: now.toISOString()
  }
}];

// âœ… POPRAWIONY KOD dla node "Process Yearly Data (FAST)"
// Merge rozpakowaÅ‚ tablicÄ™ na osobne inputy

const inputs = $input.all();
console.log('=== FINAL DEBUG ===');
console.log('Number of inputs:', inputs.length);

// âœ… KaÅ¼dy input to osobny obiekt z tablicy Merge
let params = {};
let monthlyRows = [];

inputs.forEach((input, index) => {
    console.log(`\n--- INPUT ${index} ---`);

    // SprawdÅº czy input ma wrapper .json czy jest bezpoÅ›redni
    const data = input.json || input;
    console.log('Data keys:', Object.keys(data));
    console.log('Has month:', !!data.month);
    console.log('Has total_amount:', !!data.total_amount);

    // SprawdÅº czy to parametry (ma year ale nie ma month)
    if (data.year && !data.month) {
        params = data;
        console.log('âœ… Found params:', params);
    }
    // SprawdÅº czy to dane miesiÄ™czne (ma month i total_amount)
    else if (data.month && data.total_amount !== undefined) {
        monthlyRows.push(data);
        console.log(`âœ… Found monthly data: month ${data.month} (${data.month_name}), amount ${data.total_amount} zÅ‚`);
    }
    else {
        console.log('âšª Skipped - not recognized as params or monthly data');
    }
});

console.log('\n=== EXTRACTION RESULTS ===');
console.log('Params found:', Object.keys(params).length > 0 ? 'YES' : 'NO');
console.log('Monthly rows found:', monthlyRows.length);

if (Object.keys(params).length > 0) {
    console.log('Params:', params);
}

monthlyRows.forEach(row => {
    console.log(`  ðŸ“Š Month ${row.month} (${row.month_name}): ${row.total_amount} zÅ‚`);
});

// SprawdÅº czy mamy jakiekolwiek dane miesiÄ™czne
if (monthlyRows.length === 0) {
    console.log('âŒ No monthly data found - returning empty result');
    // POPRAWKA: Zwracamy pusty obiekt zamiast pustej tablicy
    return [{ json: { months: [], error: 'No data found' } }];
}

// Sortuj wedÅ‚ug miesiÄ…ca
monthlyRows.sort((a, b) => (a.month || 0) - (b.month || 0));

const now = new Date();
const currentYear = now.getFullYear();
const currentMonth = now.getMonth() + 1;

// OkreÅ›l rok i maksymalny miesiÄ…c
const requestedYear = params.year || monthlyRows[0].year || currentYear;
const maxMonth = (requestedYear === currentYear) ? currentMonth : 12;

console.log('\n=== PROCESSING SETUP ===');
console.log('Requested year:', requestedYear);
console.log('Max month to process:', maxMonth);
console.log('Available months:', monthlyRows.map(r => r.month).sort());

// Mapuj miesiÄ…ce na odpowiedzi API
const monthNames = [
  'StyczeÅ„', 'Luty', 'Marzec', 'KwiecieÅ„', 'Maj', 'Czerwiec',
  'Lipiec', 'SierpieÅ„', 'WrzesieÅ„', 'PaÅºdziernik', 'Listopad', 'GrudzieÅ„'
];

// UtwÃ³rz mapÄ™ istniejÄ…cych danych
const dataByMonth = new Map();
monthlyRows.forEach(row => {
  dataByMonth.set(row.month, row);
});

// Przygotuj dane dla wszystkich miesiÄ™cy (1 do maxMonth)
const monthsData = [];
for (let month = 1; month <= maxMonth; month++) {
  const row = dataByMonth.get(month);

  if (row) {
    // Mamy dane - uÅ¼yj ich
    const totalAmount = parseFloat(row.total_amount) || 0;
    const privateAmount = parseFloat(row.private_amount) || 0;
    const mtHubAmount = parseFloat(row.mt_hub_amount) || 0;
    const fhuAmount = parseFloat(row.fhu_amount) || 0;
    const recordCount = parseInt(row.record_count) || 0;

    // Oblicz liczbÄ™ transakcji per kategoria proporcjonalnie
    const totalCategoryAmount = privateAmount + mtHubAmount + fhuAmount;
    let privateCount = 0;
    let mtHubCount = 0;
    let fhuCount = 0;

    if (totalCategoryAmount > 0 && recordCount > 0) {
      // Oblicz proporcje dla kaÅ¼dej kategorii
      const privateRatio = privateAmount / totalCategoryAmount;
      const mtHubRatio = mtHubAmount / totalCategoryAmount;
      const fhuRatio = fhuAmount / totalCategoryAmount;

      // Przydziel transakcje proporcjonalnie
      privateCount = Math.round(privateRatio * recordCount);
      mtHubCount = Math.round(mtHubRatio * recordCount);
      fhuCount = Math.round(fhuRatio * recordCount);

      // Skoryguj, aby suma siÄ™ zgadzaÅ‚a
      const calculatedTotal = privateCount + mtHubCount + fhuCount;
      if (calculatedTotal !== recordCount) {
        // ZnajdÅº kategoriÄ™ z najwiÄ™kszÄ… kwotÄ… i skoryguj
        if (privateAmount >= mtHubAmount && privateAmount >= fhuAmount) {
          privateCount += (recordCount - calculatedTotal);
        } else if (mtHubAmount >= fhuAmount) {
          mtHubCount += (recordCount - calculatedTotal);
        } else {
          fhuCount += (recordCount - calculatedTotal);
        }
      }

      // Upewnij siÄ™, Å¼e kaÅ¼da kategoria z kwotÄ… ma przynajmniej 1 transakcjÄ™
      if (privateAmount > 0 && privateCount === 0) privateCount = 1;
      if (mtHubAmount > 0 && mtHubCount === 0) mtHubCount = 1;
      if (fhuAmount > 0 && fhuCount === 0) fhuCount = 1;
    }

    monthsData.push({
      month: month,
      monthName: row.month_name || monthNames[month - 1],
      data: {
        monthlyExpenses: totalAmount,
        expenseCategories: [
          {
            name: 'Prywatne',
            amount: privateAmount,
            transactionCount: privateCount,
            color: '#34C759'
          },
          {
            name: 'MT HUB',
            amount: mtHubAmount,
            transactionCount: mtHubCount,
            color: '#007AFF'
          },
          {
            name: 'FHU',
            amount: fhuAmount,
            transactionCount: fhuCount,
            color: '#FF2D92'
          }
        ]
      }
    });

    console.log(`âœ… Processed month ${month} (${row.month_name}): ${totalAmount} zÅ‚ [P:${privateAmount}, M:${mtHubAmount}, F:${fhuAmount}]`);
  } else {
    // Brak danych - puste wartoÅ›ci
    monthsData.push({
      month: month,
      monthName: monthNames[month - 1],
      data: {
        monthlyExpenses: 0,
        expenseCategories: [
          { name: 'Prywatne', amount: 0, transactionCount: 0, color: '#34C759' },
          { name: 'MT HUB', amount: 0, transactionCount: 0, color: '#007AFF' },
          { name: 'FHU', amount: 0, transactionCount: 0, color: '#FF2D92' }
        ]
      }
    });

    console.log(`âšª Empty month ${month} (${monthNames[month - 1]})`);
  }
}

console.log('\n=== FINAL SUCCESS ===');
console.log(`ðŸŽ‰ Successfully prepared data for ${monthsData.length} months (${requestedYear})`);

// Podsumowanie danych
const totalExpenses = monthsData.reduce((sum, month) => sum + month.data.monthlyExpenses, 0);
const monthsWithData = monthsData.filter(month => month.data.monthlyExpenses > 0).length;
console.log(`ðŸ“Š Total yearly expenses: ${totalExpenses} zÅ‚`);
console.log(`ðŸ“Š Months with data: ${monthsWithData}/${monthsData.length}`);

// âœ… POPRAWKA: Zwracamy dane w formacie oczekiwanym przez n8n
// Opcja 1: JeÅ›li chcesz zwrÃ³ciÄ‡ wszystkie miesiÄ…ce jako jeden obiekt
return [{
  json: {
    year: requestedYear,
    months: monthsData,
    summary: {
      totalExpenses: totalExpenses,
      monthsWithData: monthsWithData,
      totalMonths: monthsData.length
    }
  }
}];

